INSERT INTO `kgtp_products` (`id`, `name`, `photo`, `quantity`, `price`, `weight`, `description`, `id_kgtp_categories`) VALUES
(1, 'BRUME PARFUMEE DARK OIL Sebastian Professional 200 ml', 'assets\\img\\toustypes1.jpg', 100, 17.49, 310, '<div class=\"container-fluid\">\r\n                <div class=\"row\">\r\n                    <div class=\"moreInfo col-md-12\">\r\n                        <h4>EN SAVOIR PLUS</h4>\r\n                    </div>\r\n                    <div class=\"description col-md-4\">\r\n                        <h4>Description </h4>\r\n                        <p class=\"text-center\">Brume Parfumée Soyeuse Dark Oil Sebastian Professional 200 ml pour un rendu brillant et soyeux adaptée à tous les types de cheveux.</p>\r\n                        <p class=\"text-center\">La Brume Parfumée Soyeuse Dark Oil Sebastian Professional 200 ml a été conçue spécialement pour apporter plus de brillance dans la chevelure et offrir un toucher soyeux aux cheveux. Il s\'agit d\'une brume légère et parfumée qui peut être utilisée en retouche tout au long de la journée ou juste après vos soins Dark Oil, avant le brushing.</p>\r\n                    </div>\r\n                    <div class=\"description col-md-4\">\r\n                        <h4>Action :</h4>\r\n                        <p class=\"text-center\"> Infusé d\'un mélange unique d\'huiles, avec de l\'huile de jojoba et de l\'huile d\'argan, vos cheveux seront parfaitement lissés et démêlés pour faciliter le coiffage. La Brume Parfumée Soyeuse Dark Oil Sebastian Professional 200 ml possède la fragrance unique de Dark Oil pour vous envoûter à chacune des applications des produits de la gamme. En associant cette brume légère aux autres produits de la gamme Dark Oil, vous offrirez à vos cheveux un soin complet ainsi qu\'un parfum unique dans votre chevelure. Ainsi, la Brume Parfumée Soyeuse Dark Oil Sebastian Professional 200 ml vous apportera une chevelure plus brillante, plus soyeuse et plus légère, sans être alourdie et cela, tout au long de la journée, avec sa fragrance unique dont vous ne pourrez plus vous passer.</p>\r\n                    </div>\r\n                    <div class=\"description col-md-4\">\r\n                        <h4>Utilisation :</h4>\r\n                        <p class=\"text-center\">Appliquez la Brume Parfumée Soyeuse Dark Oil Sebastian Professional 200 ml avant le brushing sur cheveux humide. Peut également s\'appliquer tout au long de la journée pour rafraîchir la coiffure.</p>\r\n                    </div>\r\n                </div>\r\n            </div>', 1),
(2, 'MASQUE LEGER DARK OIL Sebastian Professional 150 ml', 'assets\\img\\toustypes2.jpg', 100, 25.49, 240, '<div class=\"container-fluid\">\r\n                <div class=\"row\">\r\n                    <div class=\"moreInfo col-md-12\">\r\n                        <h4>EN SAVOIR PLUS</h4>\r\n                    </div>\r\n                    <div class=\"description col-md-4\">\r\n                        <h4>DESCRIPTION </h4>\r\n                        <p class=\"text-center\">Masque Léger Dark Oil Sebastian Professional 150 ml pour nourrir et apporter de la brillance tous les types de cheveux. Le Masque Léger Dark Oil Sebastian Professional 150 ml a été conçu spécialement pour apporter de la brillance et de la légèreté à tous les types de cheveux. Il s\'agit d\'un masque léger qui permet de nourrir en profondeur les cheveux tout en leur apportant de la brillance pour une chevelure douce comme de la soie.</p>\r\n                    </div>\r\n                    <div class=\"description col-md-4\">\r\n                        <h4>Action :</h4>\r\n                        <p class=\"text-center\"> Infusé d\'un mélange unique d\'huiles, avec de l\'huile de jojoba et de l\'huile d\'argan, vos cheveux seront parfaitement nourris tout en gardant une souplesse naturelle. Le Masque Léger Dark Oil Sebastian Professional 150 ml possède la fragrance unique de Dark Oil pour vous envoûter à chacune des applications des produits de la gamme. En associant ce masque léger aux autres produits de la gamme Dark Oil, vous obtiendrez des cheveux jusqu\'à 3 fois plus lisses, tout en légèreté. Ainsi, le Masque Léger Dark Oil Sebastian Professional 150 ml vous apportera une chevelure plus brillante, plus lisse et plus légère, sans être alourdie et cela, avec une fragrance unique dont vous ne pourrez plus vous passer.</p>\r\n                    </div>\r\n                    <div class=\"description col-md-4\">\r\n                        <h4>Utilisation :</h4>\r\n                        <p class=\"text-center\">Appliquez le Masque Léger Dark Oil Sebastian Professional 150 ml après le shampooing. Laissez agir 3 à 5 minutes puis rincez abondamment.</p>\r\n                    </div>\r\n                </div>\r\n            </div>', 1),
(3, 'CONDITIONNEUR LEGER DARK OIL Sebastian Professional 250 ml', 'assets\\img\\toustypes3.jpg', 100, 22.29, 360, ' <div class=\"container-fluid\">\r\n                <div class=\"row\">\r\n                    <div class=\"moreInfo col-md-12\">\r\n                        <h4>EN SAVOIR PLUS</h4>\r\n                    </div>\r\n                    <div class=\"description col-md-4\">\r\n                        <h4>Description </h4>\r\n                        <p class=\"text-center\">Conditionneur Léger Dark Oil Sebastian Professional 250 ml - après shampooing pour lisser, démêler et apporter du corps à tous les types de cheveux. Le Conditionneur Léger Dark Oil Sebastian Professional 250 ml a été conçu spécialement pour apporter de la brillance et de la légèreté à tous les types de cheveux, tout en apportant du corps à la chevelure.</p>\r\n                    </div>\r\n                    <div class=\"description col-md-4\">\r\n                        <h4>Action :</h4>\r\n                        <p class=\"text-center\"> Il s\'agit d\'un après shampooing qui permet de lisser et de démêler les cheveux et cela, sans alourdir la chevelure. Infusé d\'un mélange unique d\'huiles, avec de l\'huile de jojoba et de l\'huile d\'argan, vos cheveux seront parfaitement lissés et démêlés pour faciliter le coiffage. Le Conditionneur Léger Dark Oil Sebastian Professional 250 ml possède la fragrance unique de Dark Oil pour vous envoûter à chacune des applications des produits de la gamme. En associant cet après shampooing léger aux autres produits de la gamme Dark Oil, vous obtiendrez des cheveux jusqu\'à 3 fois plus lisses, tout en légèreté. Ainsi, le Conditionneur Léger Dark Oil Sebastian Professional 250 ml vous apportera une chevelure plus brillante, plus lisse et plus légère, sans être alourdie et cela, avec une fragrance unique dont vous ne pourrez plus vous passer.</p>\r\n                    </div>\r\n                    <div class=\"description col-md-4\">\r\n                        <h4>Utilisation :</h4>\r\n                        <p class=\"text-center\">Appliquez le Conditionneur Léger Dark Oil Sebastian Professional 250 ml après le shampooing puis rincez abondamment.</p>\r\n                    </div>\r\n                </div>\r\n            </div>', 1),
(4, 'SHAMPOOING LEGER DARK OIL Professional 250 ml', 'assets\\img\\toustypes4.jpg', 100, 17.59, 340, '<div class=\"container-fluid\">\r\n                    <div class=\"row\">\r\n                        <div class=\"moreInfo col-md-12\">\r\n                            <h4>EN SAVOIR PLUS</h4>\r\n                        </div>\r\n                        <div class=\"description col-md-4\">\r\n                            <h4>Description </h4>\r\n                            <p class=\"text-center\">Shampooing Léger Dark Oil Sebastian Professional 250 ml pour plus de brillance et de légèreté sans alourdir les cheveux. Adapté à tous les types de cheveux.</p>\r\n                        </div>\r\n                        <div class=\"description col-md-4\">\r\n                            <h4>Action :</h4>\r\n                            <p class=\"text-center\">Le Shampooing Léger Dark Oil Sebastian Professional 250 ml a été conçu spécialement pour apporter plus de brillance et de légèreté à tous les types de cheveux. Il s\'agit d\'un shampooing léger qui permet de nettoyer délicatement les cheveux et cela, sans les alourdir. Infusé d\'un mélange unique d\'huiles, avec de l\'huile de jojoba et de l\'huile d\'argan, vos cheveux seront parfaitement nettoyés tout en étant plus brillants et plus légers. Le Shampooing Léger Dark Oil Sebastian Professional 250 ml possède la fragrance unique de Dark Oil pour vous envoûter à chacune des applications des produits de la gamme. En associant ce shampooing léger aux autres produits de la gamme Dark Oil, vous obtiendrez des cheveux jusqu\'à 3 fois plus lisses, tout en légèreté. Ainsi, le Shampooing Léger Dark Oil Sebastian Professional 250 ml vous apportera une chevelure plus brillante, plus lisse et plus légère, sans être alourdie et cela, avec une fragrance unique dont vous ne pourrez plus vous passer.</p>\r\n                        </div>\r\n                        <div class=\"description col-md-4\">\r\n                            <h4>Utilisation :</h4>\r\n                            <p class=\"text-center\">Appliquez le Shampooing Léger Dark Oil Sebastian Professional 250 ml sur cheveux mouillés. Massez et faites mousser délicatement. Rincez abondamment et répétez l\'opération si nécessaire.</p>\r\n                        </div>\r\n                    </div>\r\n                </div>', 1),
(5, 'CREME COIFFANTE TWIXTED Sebastian Professional 145 ml ', 'assets\\img\\boucles1.jpg', 100, 20.9, 220, '<div class=\"container-fluid\">\r\n                <div class=\"row\">\r\n                    <div class=\"moreInfo col-md-12\">\r\n                        <h4>EN SAVOIR PLUS</h4>\r\n                    </div>\r\n                    <div class=\"description col-md-6\">\r\n                        <h4>Description </h4>\r\n                        <p class=\"text-center\">La crème sublimatrice de boucles pour des boucles rebondies et une définition naturelle.\r\n                            Avec la technologie FLEXI  ALG™ , elle définit les boucles en douceur et en légèreté.\r\n                            Lutte contre les frisottis et hydrate pour obtenir des boucles agréables au toucher.\r\n                            Obtenez des boucles plus fortes et une tenue longue durée.</p>\r\n                    </div>\r\n                    <div class=\"description col-md-6\">\r\n                        <h4>Technologie :</h4>\r\n                        <p class=\"text-center\">COMPLEXE  FLEXI  ALG™ technologie révolutionnaire brevetée composée de :\r\n                            - Extraits d’algues rouges\r\n                            - Polymères élastiques\r\n                            - Panthénol (pro-vitamine B)\r\n                            - Agents hydratants</p>\r\n                    </div>\r\n                </div>\r\n            </div>', 2),
(6, 'MASQUE TWISTED Sebastian Professional 150 ml', 'assets\\img\\boucles2.jpg', 100, 19.95, 220, '<div class=\"container-fluid\">\r\n                <div class=\"row\">\r\n                    <div class=\"moreInfo col-md-12\">\r\n                        <h4>EN SAVOIR PLUS</h4>\r\n                    </div>\r\n                    <div class=\"description col-md-4\">\r\n                        <h4>Description </h4>\r\n                        <p class=\"text-center\">Nourrit et renforce la fibre capillaire, tout en la protégeant de l’humidité.\r\n                            Lutte contre les frisottis, hydrate et définie les boucles.\r\n                            Grâce à la technologie FLEXI  ALG™, donnez à vos cheveux bouclés de l\'élasticité, du ressort, de l\'hydratation, de la définition et une protection anti-frisottis.</p>\r\n                    </div>\r\n                    <div class=\"description col-md-4\">\r\n                    <h4>Application :</h4>\r\n                        <p class=\"text-center\">Après avoir nettoyé vos cheveux avec le shampooing TWISTED\r\n                            Appliquez le masque généreusement sur les longueurs et pointes.\r\n                            Laisser pauser 3 à 5 minutes, puis rincer\r\n                            Utilisez le masque Twisted 1 à 2 fois par semaine.</p>\r\n                    </div>\r\n                    <div class=\"description col-md-4\">\r\n                        <h4>Technologie :</h4>\r\n                        <p class=\"text-center\">COMPLEXE  FLEXI  ALG™ technologie révolutionnaire brevetée composée de :\r\n                            - Extraits d’algues rouges\r\n                            - Polymères élastiques\r\n                            - Panthénol (pro-vitamine B)\r\n                            - Agents hydratants</p>\r\n                    </div>\r\n                </div>\r\n            </div>', 2),
(7, 'MOUSSE COIFFANTE TWISTED Sebastian Professional 200 ml', 'assets\\img\\boucles3.jpg', 100, 19.95, 310, '<div class=\"container-fluid\">\r\n                <div class=\"row\">\r\n                    <div class=\"moreInfo col-md-12\">\r\n                        <h4>EN SAVOIR PLUS</h4>\r\n                    </div>\r\n                    <div class=\"description col-md-4\">\r\n                        <h4>Description </h4>\r\n                        <p class=\"text-center\">La mousse coiffante Twisted Sebastian, rehasseur de boucles.\r\n                            Pour des boucles toniques sans effet carton.\r\n                            Avec la technologie FLEXI  ALG™, renforcez la souplesse de vos cheveux et contrôlez les frisottis pour des boucles rebondies.</p>\r\n                    </div>\r\n                    <div class=\"description col-md-4\">\r\n                    <h4>Application :</h4>\r\n                    <p class=\"text-center\">Sur cheveux humides, appliquez le produit en froissant les cheveux.\r\n                            Séchez-les pour intensifier les boucles.\r\n                            Appliquez sur cheveux secs et stylisez les boucles avec des appareils chauffants pour créer un look longue durée.</p>\r\n                    </div>\r\n                    <div class=\"description col-md-4\">\r\n                        <h4>Technologie :</h4>\r\n                        <p class=\"text-center\">COMPLEXE  FLEXI  ALG™ technologie révolutionnaire brevetée composée de :\r\n                            - Extraits d’algues rouges\r\n                            - Polymères élastiques\r\n                            - Panthénol (pro-vitamine B)\r\n                            - Agents hydratants</p>\r\n                    </div>\r\n                </div>\r\n            </div>', 2),
(8, 'SHAMPOOING TWISTED Sebastian Professional 250 ml', 'assets\\img\\boucles4.jpg', 100, 16.2, 350, '<div class=\"container-fluid\">\r\n                <div class=\"row\">\r\n                    <div class=\"moreInfo col-md-12\">\r\n                        <h4>EN SAVOIR PLUS</h4>\r\n                    </div>\r\n                    <div class=\"description col-md-4\">\r\n                        <h4>Description </h4>\r\n                        <p class=\"text-center\">Le Shampooing Twisted Sebastian, pour cheveux bouclés ou ondulés nettoie délicatement les cheveux tout en les hydratant et en apportant à chaque mèche une élasticité facilitant le coiffage.Grâce à la technologie FLEXI  ALG™, donnez à vos cheveux bouclés de l\'élasticité, du ressort, de l\'hydratation, de la définition et une protection anti-frisottis.\r\n                            Obtenez des boucles plus fortes et une tenue longue durée.</p>\r\n                    </div>\r\n                    <div class=\"description col-md-4\">\r\n                    <h4>Application :</h4>\r\n                    <p class=\"text-center\">Appliquez sur cheveux mouillés.\r\n                            Faites mousser puis rincer abondamment.\r\n                            Répétez l’action si nécessaire.</p>\r\n                    </div>\r\n                    <div class=\"description col-md-4\">\r\n                        <h4>Technologie :</h4>\r\n                        <p class=\"text-center\">COMPLEXE  FLEXI  ALG™ technologie révolutionnaire brevetée composée de :\r\n                            - Extraits d’algues rouges\r\n                            - Polymères élastiques\r\n                            - Panthénol (pro-vitamine B)\r\n                            - Agents hydratants</p>\r\n                    </div>\r\n                </div>\r\n            </div>', 2),
(9, 'SHAMPOOING IGNITE COLOR MONO Sebastian 1000 ml', 'assets\\img\\colores1.jpg', 100, 40, 1190, '<div class=\"container-fluid\">\r\n                <div class=\"row\">\r\n                    <div class=\"moreInfo col-md-12\">\r\n                        <h4>EN SAVOIR PLUS</h4>\r\n                    </div>\r\n                    <div class=\"description col-md-4\">\r\n                        <h4>Description </h4>\r\n                        <p class=\"text-center\">Le Shampooing Twisted Sebastian, pour cheveux bouclés ou ondulés nettoie délicatement les cheveux tout en les hydratant et en apportant à chaque mèche une élasticité facilitant le coiffage.Grâce à la technologie FLEXI  ALG™, donnez à vos cheveux bouclés de l\'élasticité, du ressort, de l\'hydratation, de la définition et une protection anti-frisottis.\r\n                            Obtenez des boucles plus fortes et une tenue longue durée.</p>\r\n                    </div>\r\n                    <div class=\"description col-md-4\">\r\n                    <h4>Application :</h4>\r\n                    <p class=\"text-center\">Appliquez sur cheveux mouillés.\r\n                            Faites mousser puis rincer abondamment.\r\n                            Répétez l’action si nécessaire.</p>\r\n                    </div>\r\n                    <div class=\"description col-md-4\">\r\n                        <h4>Technologie :</h4>\r\n                        <p class=\"text-center\">COMPLEXE  FLEXI  ALG™ technologie révolutionnaire brevetée composée de :\r\n                            - Extraits d’algues rouges\r\n                            - Polymères élastiques\r\n                            - Panthénol (pro-vitamine B)\r\n                            - Agents hydratants</p>\r\n                    </div>\r\n                </div>\r\n            </div>', 3),
(10, 'SHAMPOOING IGNITE COLOR MULTI Sebastian 250 ml', 'assets\\img\\colores2.jpg', 100, 15.1, 350, '<div class=\"container-fluid\">\r\n                <div class=\"row\">\r\n                    <div class=\"moreInfo col-md-12\">\r\n                        <h4>EN SAVOIR PLUS</h4>\r\n                    </div>\r\n                    <div class=\"description col-md-4\">\r\n                        <h4>Description </h4>\r\n                        <p class=\"text-center\">Shampooing Color Ignite Multi Sebastian 250 ml pour cheveux éclaircis, méchés ou avec plusieurs nuances.</p>\r\n                    </div>\r\n                    <div class=\"description col-md-4\">\r\n                        <h4>Action :</h4>\r\n                        <p class=\"text-center\">Le Shampooing Color Ignite Multi Sebastian a été conçu spécialement pour la protection de la couleur des cheveux.\r\n                            C\'est un shampooing protecteur de couleur pour cheveux éclaircis et colorés à plusieurs nuances.\r\n                            Il s\'applique facilement et nettoie en douceur les cheveux colorés.\r\n                            Il aide à protéger les cheveux des agressions extérieures tout en prolongeant l\'éclat de la coloration.\r\n                            Grâce à son complexe intelligent d\'hydratation, le Shampooing Color Ignite Multi Sebastian contient de l\'extrait de Grenade et offrira un éclat et une brillance instantanée aux cheveux colorés.\r\n                            Il cible les longueurs colorées afin d\'hydrater et restructurer les fibres capillaires en profondeur là où elles en ont le plus besoin.\r\n                            Spécialement pour les cheveux éclaircis et colorés à plusieurs nuances, le Shampooing Color Ignite Multi Sebastian sera idéal pour apporter toute la beauté que les cheveux méritent et la protection de la coloration à plusieurs nuances.</p>\r\n                    </div>\r\n                    <div class=\"description col-md-4\">\r\n                        <h4>Utilisation :</h4>\r\n                        <p class=\"text-center\">Appliquez sur cheveux mouillés.\r\n                            Massez puis rincez abondamment.\r\n                            Recommencez si nécessaire.</p>\r\n                    </div>\r\n                </div>\r\n            </div>', 3),
(11, 'SHAMPOOING IGNITE COLOR MONO Sebastian 250 ml', 'assets\\img\\colores3.jpg', 100, 15.1, 360, '<div class=\"container-fluid\">\r\n                <div class=\"row\">\r\n                    <div class=\"moreInfo col-md-12\">\r\n                        <h4>EN SAVOIR PLUS</h4>\r\n                    </div>\r\n                    <div class=\"description col-md-4\">\r\n                        <h4>Description </h4>\r\n                        <p class=\"text-center\">Shampooing Color Ignite Mono Sebastian 250 ml pour cheveux uniformément colorés.</p>\r\n                    </div>\r\n                    <div class=\"description col-md-4\">\r\n                        <h4>Action :</h4>\r\n                        <p class=\"text-center\">Le Shampooing Color Ignite Mono Sebastian 250 ml a été conçu spécialement pour répondre aux besoins des cheveux uniformément colorés. Il s\'agit d\'un shampooing qui permet d\'apporter une brillance durable ainsi qu\'une couleur éclatante aux cheveux qui résiste longtemps. Composé d\'un complexe intelligent  lissant et fixateur de couleur, le Shampooing Color Ignite Mono Sebastian 250 ml apporte une brillance uniforme et de l\'éclat à la couleur durablement. Ce shampooing pour cheveux colorés permet de nettoyer les cheveux tout en respectant la couleur. Associé au Conditionneur Color Ignite Mono, vous obtiendrez un résultat optimal. Les cuticules seront gainés et cela, pour prolonger l\'éclat de la coloration, tout en la rendant plus brillante. Ainsi, le Shampooing Color Ignite Mono Sebastian 250 ml permet de laver en douceur les cheveux colorés, tout en les rendant plus beaux, comme à la sortie de votre salon de coiffure.</p>\r\n                    </div>\r\n                    <div class=\"description col-md-4\">\r\n                        <h4>Utilisation :</h4>\r\n                        <p class=\"text-center\">Appliquez le Shampooing Color Ignite Mono Sebastian 250 ml sur cheveux humides. Massez et rincez abondamment. Répétez l\'opération si nécessaire.</p>\r\n                    </div>\r\n                </div>\r\n            </div>', 3),
(12, 'CONDITIONNEUR COLOR Sebastian 250 ml', 'assets\\img\\colores4.jpg', 100, 22.1, 360, '<div class=\"container-fluid\">\r\n                <div class=\"row\">\r\n                    <div class=\"moreInfo col-md-12\">\r\n                        <h4>EN SAVOIR PLUS</h4>\r\n                    </div>\r\n                    <div class=\"description col-md-12\">\r\n                        <h4>Description </h4>\r\n                        <p class=\"text-center\">Conditionneur Color entretient votre coloration</p>\r\n                    </div>\r\n                </div>\r\n            </div>', 3),
(13, 'CONDITIONNEUR PENETRAITT Sebastian 250 ml', 'assets\\img\\abimes1.jpg', 100, 15.7, 340, '<div class=\"container-fluid\">\r\n                <div class=\"row\">\r\n                    <div class=\"moreInfo col-md-12\">\r\n                        <h4>EN SAVOIR PLUS</h4>\r\n                    </div>\r\n                    <div class=\"description col-md-4\">\r\n                        <h4>Description </h4>\r\n                        <p class=\"text-center\">Conditionneur Penetraitt Sebastian 250 ml - après shampooing fortifiant pour cheveux abîmés.</p>\r\n                    </div>\r\n                    <div class=\"description col-md-4\">\r\n                        <h4>Action :</h4>\r\n                        <p class=\"text-center\">Le Conditionneur Penetraitt Sebastian 250 ml a été conçu spécialement pour répondre aux besoins des cheveux abîmés. Il s\'agit d\'un après shampooing pour protéger les cheveux abîmés contre les agressions et les fortifier. Il possède une hydratation intense et a été formulé spécialement pour les cheveux secs et colorés afin de les rendre plus doux au toucher. Le Conditionneur Penetraitt Sebastian 250 ml permet, ainsi, de fortifier les cheveux fragilisés tout en leur redonnant de l\'éclat. Il apporte de l\'hydratation en profondeur tout en démêlant les cheveux indisciplinés et en redonnant de l\'éclat aux cheveux ternes. Associé au Shampooing Penetraitt ou au Masque Penetraitt, cet après shampooing vous offrira un résultat optimal. Le Conditionneur Penetraitt Sebastian 250 ml vous permet donc de retrouver des cheveux plus forts, hydratés, plus faciles à démêler tout en étant protégés des agressions. Il est également idéal pour intensifier l\'éclat des cheveux colorés et leur redonner de la brillance.\r\n                    </div>\r\n                    <div class=\"description col-md-4\">\r\n                        <h4>Utilisation :</h4>\r\n                        <p class=\"text-center\">Essorez les cheveux et appliquez le Conditionneur Penetraitt Sebastian 250 ml sur les cheveux en insistant sur les pointes. Rincez abondamment.</p>\r\n                    </div>\r\n                </div>\r\n            </div>', 4),
(14, 'SHAMPOOING PENETRAIT SEBASTIAN 250 ml', 'assets\\img\\abimes2.jpg', 100, 15.7, 360, '<div class=\"container-fluid\">\r\n                <div class=\"row\">\r\n                    <div class=\"moreInfo col-md-12\">\r\n                        <h4>EN SAVOIR PLUS</h4>\r\n                    </div>\r\n                    <div class=\"description col-md-4\">\r\n                        <h4>Description </h4>\r\n                        <p class=\"text-center\">Shampooing Penetraitt Sebastian 250 ml fortifiant pour les cheveux abîmés.</p>\r\n                    </div>\r\n                    <div class=\"description col-md-4\">\r\n                        <h4>Action :</h4>\r\n                        <p class=\"text-center\">Le Shampooing Penetraitt Sebastian 250 ml a été conçu spécialement pour répondre aux besoins des cheveux abîmés. Il s\'agit d\'un shampooing fortifiant et réparateur en profondeur pour les cheveux abîmés. Ce shampooing fortifiant possède le système réparateur proffesionnel qui permet de pénétrer en profondeur dans le cheveu en éliminant les impuretés tout en fortifiant les cheveux abîmés. Les cheveux se fragilisent par les traitements chimiques, la chaleur ou encore les éléments environnementaux. C\'est pour cela que le Shampooing Penetraitt Sebastian 250 ml est parfaitement adapté à tous les types de cheveux abîmés, il va agir en réparant et en fortifiant les cheveux en profondeur. Vous retrouverez, ainsi, des cheveux parfaitement lavés et propres, plus doux, plus volumineux et plus forts. Sans danger pour les cheveux colorés, le Shampooing Penetraitt Sebastian 250 ml est recommandé pour apporter de la brillance et plus d\'intensité à la couleur. Associé au Conditionneur Penetraitt ou au Masque Penetraitt, apportez un soin complet et optimal pour retrouver des cheveux plus forts et plus beaux.\r\n                    </div>\r\n                    <div class=\"description col-md-4\">\r\n                        <h4>Utilisation :</h4>\r\n                        <p class=\"text-center\">Massez le Shampooing Penetraitt Sebastian 250 ml sur cheveux humides pour activer la mousse onctueuse. Rincez abondamment. Renouvelez l\'opération si nécessaire. Poursuivez avec le Conditionneur Penetraitt.</p>\r\n                    </div>\r\n                </div>\r\n            </div>', 4),
(15, 'MASQUE PENETRAITT Sebastian 150 ml', 'assets\\img\\abimes3.jpg', 100, 19.5, 260, '<div class=\"container-fluid\">\r\n                <div class=\"row\">\r\n                    <div class=\"moreInfo col-md-12\">\r\n                        <h4>EN SAVOIR PLUS</h4>\r\n                    </div>\r\n                    <div class=\"description col-md-4\">\r\n                        <h4>Description </h4>\r\n                        <p class=\"text-center\">Masque Penetraitt Sebastian 150 ml intense réparateur et fortifiant pour cheveux abîmés.</p>\r\n                    </div>\r\n                    <div class=\"description col-md-4\">\r\n                        <h4>Action :</h4>\r\n                        <p class=\"text-center\">Le Masque Penetraitt Sebastian 150 ml a été conçu spécialement pour répondre aux besoins des cheveux abîmés. Il s\'agit d\'un masque traitement intense réparateur et fortifiant pour renforcer vos cheveux abîmés contre les agressions. Ce produit permet de réparer en profondeur les cheveux fragilisés et traités chimiquement lorsqu\'ils sont le plus vulnérables, tout en les fortifiants. Composé d\'un système réparateur actif, le Masque Penetraitt Sebastian 150 ml va apporter de la douceur et de la brillance aux cheveux secs et ternes. Associé au Shampooing Penetraitt ou au Conditionneur Penetraitt, obtenez un résultat optimal. Ainsi, le Masque Penetraitt Sebastian 150 ml vous apportera toute l\'hydratation dont vos cheveux abîmés ont besoin, ainsi qu\'une réparation en profondeur et un cheveux plus fort. Il est également idéal et recommandé pour renforcer l\'intensité des cheveux colorés ainsi que leur éclat, pour des cheveux en bonne santé et aussi beaux qu\'à la sortie de votre salon de coiffure.\r\n                    </div>\r\n                    <div class=\"description col-md-4\">\r\n                        <h4>Utilisation :</h4>\r\n                        <p class=\"text-center\">Éliminez l\'excès d\'eau du cheveu, appliquez le Masque Penetraitt Sebastian 150 ml et laissez agir 2 à 3 minutes. Rincez abondamment.</p>\r\n                    </div>\r\n                </div>\r\n            </div>', 4),
(16, 'PACK PENETRAITT Sebastian', 'assets\\img\\abimes4.jpg', 100, 45, 870, '<div class=\"container-fluid\">\r\n                <div class=\"row\">\r\n                    <div class=\"moreInfo col-md-12\">\r\n                        <h4>EN SAVOIR PLUS</h4>\r\n                    </div>\r\n                    <div class=\"description col-md-12\">\r\n                        <h4>Description </h4>\r\n                        <p class=\"text-center\">Lot de 3 produits pour cheveux abimes</p>\r\n                    </div>\r\n                </div>\r\n            </div>', 4),
(17, 'CONDITIONNEUR HYDRE Sebastian Professional 250 ml', 'assets\\img\\secs1.jpg', 100, 19.49, 360, '<div class=\"container-fluid\">\r\n                <div class=\"row\">\r\n                    <div class=\"moreInfo col-md-12\">\r\n                        <h4>EN SAVOIR PLUS</h4>\r\n                    </div>\r\n                    <div class=\"description col-md-4\">\r\n                        <h4>Description </h4>\r\n                        <p class=\"text-center\">Conditionneur Hydre Sebastian Professional 250 ml - conditionneur hydratant pour les cheveux secs.</p>\r\n                    </div>\r\n                    <div class=\"description col-md-4\">\r\n                        <h4>Action :</h4>\r\n                        <p class=\"text-center\">Le Conditionneur Hydre Sebastian Professional 250 ml a été conçu spécialement pour répondre aux besoins des cheveux secs. Il s\'agit d\'un après shampooing hydratant et nourrissant qui permettra de raviver les cheveux secs et frisottant. Les ingrédients présents dans ce conditionneur seront activés au contact de l\'eau. Extrêmement hydratant, l\'après shampooing va pénétrer instantanément dans la chevelure. Ainsi, grâce au Conditionneur Hydre Sebastian Professional 250 ml, les cheveux secs et les cheveux traités chimiquement seront donc parfaitement hydratés et nourris en profondeur. Le Conditionneur Hydre Sebastian Professional 250 ml permettra également de démêler plus facilement les cheveux tout en les revigorant et en leur apportant un mouvement naturel et un effet soyeux. Retrouvez des cheveux avec plus de corps, de douceur et de légèreté. Ce produit est sans danger pour les cheveux colorés et sera adapté à tous les types de cheveux secs. Hydratez vos cheveux secs avec cet après shampooing Sebastian.\r\n                    </div>\r\n                    <div class=\"description col-md-4\">\r\n                        <h4>Utilisation :</h4>\r\n                        <p class=\"text-center\">Essorez les cheveux et appliquez le Conditionneur Hydre Sebastian Professional 250 ml sur les cheveux, en insistant sur les pointes. Rincez abondamment.</p>\r\n                    </div>\r\n                </div>\r\n            </div>', 5),
(18, 'MASQUE HYDRE TREATMENT Sebastian 150 ml', 'assets\\img\\secs2.jpg', 100, 19, 240, '<div class=\"container-fluid\">\r\n                <div class=\"row\">\r\n                    <div class=\"moreInfo col-md-12\">\r\n                        <h4>EN SAVOIR PLUS</h4>\r\n                    </div>\r\n                    <div class=\"description col-md-4\">\r\n                        <h4>Description </h4>\r\n                        <p class=\"text-center\">Masque Hydre Treatment Sebastian 150 ml intense hydratant pour les cheveux secs.</p>\r\n                    </div>\r\n                    <div class=\"description col-md-4\">\r\n                        <h4>Action :</h4>\r\n                        <p class=\"text-center\">Le Masque Hydre Treatment Sebastian 150 ml a été conçu spécialement pour répondre aux besoins des cheveux secs et abîmés. Il s\'agit d\'un masque traitement intense et hydratant qui agit en profondeur. Il permet de revitaliser, d\'hydrater et de rendre les cheveux soyeux pour retrouver une chevelure en pleine santé. Ce masque traitement agit sur les cheveux secs et les cheveux traités chimiquement, et convient parfaitement aux cheveux colorés. Grâce au Masque Hydre Treatment Sebastian 150 ml, même les cheveux frisés retrouvent une hydratation intense, des cheveux soyeux et plus doux. Associé au Shampooing Hydre, obtenez un résultat optimal. Ainsi, le Masque Hydre Treatment Sebastian 150 ml vous permettra de faciliter le démêlage tout en retrouvant une hydratation intense en profondeur ainsi que des cheveux parfaitement revitalisés, pour une chevelure plus soyeuse, plus douce et en pleine santé, comme à la sortie de votre salon de coiffure.\r\n                    </div>\r\n                    <div class=\"description col-md-4\">\r\n                        <h4>Utilisation :</h4>\r\n                        <p class=\"text-center\">Appliquez le Masque Hydre Treatment Sebastian 150 ml sur cheveux propres et rincés. Laissez agir 3 à 5 minutes. Rincez abondamment. Utilisez une ou deux fois par semaine.</p>\r\n                    </div>\r\n                </div>\r\n            </div>', 5),
(19, 'SHAMPOOING HYDRE Sebastian <br/>250 ml', 'assets\\img\\secs3.jpg', 100, 15.7, 340, '<div class=\"container-fluid\">\r\n                <div class=\"row\">\r\n                    <div class=\"moreInfo col-md-12\">\r\n                        <h4>EN SAVOIR PLUS</h4>\r\n                    </div>\r\n                    <div class=\"description col-md-4\">\r\n                        <h4>Description </h4>\r\n                        <p class=\"text-center\">Shampooing Hydre Sebastian 250 ml pour hydrater et donner du corps aux cheveux secs.</p>\r\n                    </div>\r\n                    <div class=\"description col-md-4\">\r\n                        <h4>Action :</h4>\r\n                        <p class=\"text-center\">Le Shampooing Hydre Sebastian 250 ml a été conçu spécialement pour répondre aux besoins des cheveux secs. Il s\'agit d\'un shampooing hydratant qui apportent du corps aux cheveux. Composé d\'une formule intense et nourrissante, ce shampooing hydratant réhydrate les cheveux secs, ayant tendance à frisotter ou les cheveux traités chimiquement. Le Shampooing Hydre Sebastian 250 ml vous permettra ainsi d\'apporter une texture à vos cheveux et une brillance soyeuse. Adapté aux cheveux colorés, il est ,ainsi, sans risque pour votre couleur. Associé au Conditionneur Hydre, vous obtiendrez un résultat optimal. Si vos cheveux sont secs, déshydratés ou fatigués et nécessitent une hydratation intense sans risque pour votre couleur, vous pouvez aussi associer le Masque Hydre, pour un soin complet d\'hydratation. Ainsi, le Shampooing Hydre Sebastian 250 ml vous apportera toute l\'hydratation dont vos cheveux secs ont besoin tout en les texturisant et en leur donnant du corps naturellement. Retrouvez une chevelure parfaitement hydratée, brillante et soyeuse.\r\n                    </div>\r\n                    <div class=\"description col-md-4\">\r\n                        <h4>Utilisation :</h4>\r\n                        <p class=\"text-center\">Appliquez le Shampooing Hydre Sebastian 250 ml sur cheveux humides en massant doucement pour faire mousser. Rincez abondamment. Répétez l\'opération si nécessaire. Associez ce shampooing au Conditionneur Hydre et si vos cheveux sont secs, déshydratés ou fatigués et nécessitent une hydratation sans risque pour la couleur, utilisez le Masque Hydre.</p>\r\n                    </div>\r\n                </div>\r\n            </div>', 5),
(20, 'PACK TREATMENT Sebastian 150 ml', 'assets\\img\\secs4.jpg', 100, 45, 940, '<div class=\"container-fluid\">\r\n                <div class=\"row\">\r\n                    <div class=\"moreInfo col-md-12\">\r\n                        <h4>EN SAVOIR PLUS</h4>\r\n                    </div>\r\n                    <div class=\"description col-md-12\">\r\n                        <h4>Description </h4>\r\n                        <p class=\"text-center\">Pack de 3 produits pour cheveux secs</p>\r\n                    </div>\r\n                </div>\r\n            </div>', 5),
(21, 'GEL FORTE Sebastian 200 ml', 'assets\\img\\coiffants1.jpg', 100, 18, 310, '<div class=\"container-fluid\">\r\n                <div class=\"row\">\r\n                    <div class=\"moreInfo col-md-12\">\r\n                        <h4>EN SAVOIR PLUS</h4>\r\n                    </div>\r\n                    <div class=\"description col-md-6\">\r\n                        <h4>Description </h4>\r\n                        <p class=\"text-center\">Formule pour les cheveux sculptés et une brillance, sans avoir une sensation collante ou dur</p>\r\n                    </div>\r\n                    <div class=\"description col-md-6\">\r\n                        <h4>Utilisation :</h4>\r\n                        <p class=\"text-center\">Etalez sur les cheveux humides.\r\n                            Sécher au sèche-cheveux pour avoir une douceur et un corps flexible.\r\n                            Appliquer sur cheveux secs pour plus de séparation.\r\n                            Une formule facile à rincer.\r\n                            Pour tous les types de cheveux.</p>\r\n                    </div>\r\n                </div>\r\n            </div>', 6),
(22, 'TEXTURIZED Sebastian 150 ml', 'assets\\img\\coiffants2.jpg', 100, 17.1, 260, '<div class=\"container-fluid\">\r\n                <div class=\"row\">\r\n                    <div class=\"moreInfo col-md-12\">\r\n                        <h4>EN SAVOIR PLUS</h4>\r\n                    </div>\r\n                    <div class=\"description col-md-4\">\r\n                        <h4>Description </h4>\r\n                        <p class=\"text-center\">Gel liquide qui ajoute de la texture et du mouvement.</p>\r\n                    </div>\r\n                    <div class=\"description col-md-4\">\r\n                        <h4>Caractéristiques :</h4>\r\n                        <p class=\"text-center\">Formulé avec des protéines de soja et de blé donne une tenue souple et le corps à tous les types de cheveux.\r\n                            Ajouter la définition de brins, crée des styles moulés ou améliorer les vagues et les boucles avec ce gel .</p>\r\n                    </div>\r\n                    <div class=\"description col-md-4\">\r\n                        <h4>Utilisation :</h4>\r\n                        <p class=\"text-center\">Appliquer sur cheveux secs ou humides</p>\r\n                    </div>\r\n                </div>\r\n            </div>', 6),
(23, 'CRAFT CLAY Sebastian 50ml', 'assets\\img\\coiffants3.jpg', 100, 20.7, 95, '<div class=\"container-fluid\">\r\n                <div class=\"row\">\r\n                    <div class=\"moreInfo col-md-12\">\r\n                        <h4>EN SAVOIR PLUS</h4>\r\n                    </div>\r\n                    <div class=\"description col-md-4\">\r\n                        <h4>Description </h4>\r\n                        <p class=\"text-center\">Donne une finition palpable, en finition mat.</p>\r\n                    </div>\r\n                    <div class=\"description col-md-4\">\r\n                        <h4>Caractéristiques :</h4>\r\n                        <p class=\"text-center\">Avec des minéraux de terres et une épaisse texture rugueuse cela vous permet de donner du volume aux racines, des brins séparés et ajouter de la texture matte crue. Idéal pour ajouter de la texture à toutes les longueurs de cheveux, ce qui fonctionne le mieux pour le volume sur le court de longueurs moyennes.\r\n                    </div>\r\n                    <div class=\"description col-md-4\">\r\n                        <h4>Utilisation :</h4>\r\n                        <p class=\"text-center\">Travailler dans les mains puis appliquer sur les cheveux secs, séparée et sculpter.</p>\r\n                    </div>\r\n                </div>\r\n            </div>', 6),
(24, 'VOLUPT SPRAY 50ml Sebastian', 'assets\\img\\coiffants4.jpg', 100, 20.7, 95, '<div class=\"container-fluid\">\r\n                <div class=\"row\">\r\n                    <div class=\"moreInfo col-md-12\">\r\n                        <h4>EN SAVOIR PLUS</h4>\r\n                    </div>\r\n                    <div class=\"description col-md-4\">\r\n                        <h4>Description </h4>\r\n                        <p class=\"text-center\">Spray constructeur de volume</p>\r\n                    </div>\r\n                    <div class=\"description col-md-4\">\r\n                        <h4>Caractéristiques :</h4>\r\n                        <p class=\"text-center\">Faire tremper dans la phase de coiffure avec la technologie des particules qui combine le volume avec la douceur la plus sensuelle.\r\n                    </div>\r\n                    <div class=\"description col-md-4\">\r\n                        <h4>Utilisation :</h4>\r\n                        <p class=\"text-center\">Bien agiter pour activer la formule de deux phases. Vaporiser sur cheveux humides.\r\n                            Faire votre brushing pour plus de volume ou laissez à l\'air libre pour faire les boucles.</p>\r\n                    </div>\r\n                </div>\r\n            </div>', 6);

